const fiveMinutesAgo = new Date(Date.now() - (5 * 60 * 1000));

const simulatedLogs = [];

const logTypes = [
  {
    type: 'ssh_failed',
    pattern: 'Failed password for',
    count: Math.floor(Math.random() * 8) + 1, // 1-8 intentos
    template: (i) => `${new Date(Date.now() - i * 30000).toISOString()} server1 sshd[${1234 + i}]: Failed password for invalid user attacker${i} from 192.168.1.${100 + i} port 22`
  },
  {
    type: 'sudo_usage', 
    pattern: 'sudo: session opened',
    count: Math.floor(Math.random() * 3) + 1,
    template: (i) => `${new Date(Date.now() - i * 60000).toISOString()} server1 sudo: pam_unix(sudo:session): session opened for user root by admin`
  },
  {
    type: 'iptables_drop',
    pattern: 'iptables.*DROP',
    count: Math.floor(Math.random() * 5) + 1,
    template: (i) => `${new Date(Date.now() - i * 45000).toISOString()} firewall kernel: iptables: DROP IN=eth0 SRC=10.0.0.${i} DST=192.168.1.10 DPT=22`
  },
  {
    type: 'sql_injection',
    pattern: 'union.*select',
    count: Math.random() > 0.7 ? 1 : 0,
    template: () => `${new Date().toISOString()} web-server apache2: 192.168.1.50 - - [10/Dec/2024:14:30:25 +0000] "GET /index.php?id=1' UNION SELECT NULL-- HTTP/1.1" 200 512`
  }
];

// Generar logs
logTypes.forEach(logType => {
  for (let i = 0; i < logType.count; i++) {
    const logTime = new Date(Date.now() - i * 30000);
    if (logTime >= fiveMinutesAgo) {
      simulatedLogs.push({
        timestamp: logTime.toISOString(),
        raw: logType.template(i),
        file: logType.type === 'ssh_failed' ? 'failed_ssh.log' : 'alerts.log',
        source: 'simulated'
      });
    }
  }
});

if (simulatedLogs.length === 0) {
  simulatedLogs.push({
    timestamp: new Date().toISOString(),
    raw: `${new Date().toISOString()} system: No security events in last 5 minutes`,
    file: 'system.log',
    source: 'test'
  });
}

console.log(`Generated ${simulatedLogs.length} simulated logs for testing`);
return simulatedLogs.map(log => ({ json: log }));

const items = $input.all();

const parsedLogs = items.map(item => {
  const log = item.json;
  let parsed = {
    timestamp: log.timestamp,
    raw: log.raw,
    file: log.file,
    source: log.source,
    type: 'unknown'
  };

  const rawLower = log.raw.toLowerCase();

  if (rawLower.includes('failed password')) {
    const sshMatch = log.raw.match(/Failed password for (?:invalid user )?(\w+) from ([\d.]+) port (\d+)/i);
    parsed.type = 'ssh_failed';
    parsed.username = sshMatch ? sshMatch[1] : 'unknown';
    parsed.source_ip = sshMatch ? sshMatch[2] : 'unknown';
    parsed.port = sshMatch ? sshMatch[3] : 'unknown';
    parsed.category = 'authentication_failure';
    parsed.severity = 'medium';
  }

  else if (rawLower.includes('sudo') && rawLower.includes('session opened')) {
    const sudoMatch = log.raw.match(/session opened for user (\w+) by (\w+)/i);
    parsed.type = 'sudo_usage';
    parsed.user = sudoMatch ? sudoMatch[1] : 'unknown';
    parsed.by_user = sudoMatch ? sudoMatch[2] : 'unknown';
    parsed.category = 'privilege_escalation';
    parsed.severity = 'low';
  }

  else if (rawLower.includes('iptables') && rawLower.includes('drop')) {
    const iptablesMatch = log.raw.match(/SRC=([\d.]+).*DPT=(\d+)/i);
    parsed.type = 'iptables_drop';
    parsed.source_ip = iptablesMatch ? iptablesMatch[1] : 'unknown';
    parsed.dest_port = iptablesMatch ? iptablesMatch[2] : 'unknown';
    parsed.category = 'network_attack';
    parsed.severity = 'high';
  }

  else if (rawLower.includes('union') || rawLower.includes('select') || rawLower.includes('drop table')) {
    parsed.type = 'sql_injection_attempt';
    parsed.category = 'web_attack';
    parsed.severity = 'high';
  }

  else if ((rawLower.includes('admin') || rawLower.includes('wp-admin') || rawLower.includes('.env')) && rawLower.includes('404')) {
    parsed.type = 'directory_scan';
    parsed.category = 'reconnaissance';
    parsed.severity = 'medium';
  }

  return { json: parsed };
});

const filteredLogs = parsedLogs.filter(log => log.json.type !== 'unknown');

console.log(`Parsed ${filteredLogs.length} security events out of ${parsedLogs.length} total logs`);
return filteredLogs;

