async function processWithPostgreSQL() {
  try {
    // Configuración de conexión
    const postgresConfig = {
      host: 'postgres',
      port: 5432,
      database: 'security_monitoring',
      user: 'db_user',
      password: 'db_pass'
    };

    // En n8n, necesitas cargar el módulo pg así:
    const pg = require('pg');
    const client = new pg.Client(postgresConfig);

    await client.connect();
    console.log('Connected to PostgreSQL');

    const result = await client.query(
      'SELECT * FROM detection_rules WHERE enabled = true ORDER BY severity DESC'
    );

    const rules = result.rows;
    console.log(`Loaded ${rules.length} rules from database`);

    // Si no hay reglas, usar reglas por defecto
    if (rules.length === 0) {
      console.log('No rules in DB, using default rules');
      rules.push(...[
        {
          id: 1,
          name: 'SSH Brute Force',
          pattern: 'Failed password',
          severity: 'high',
          threshold: 3,
          time_window: 300,
          description: 'Multiple failed SSH attempts'
        },
        {
          id: 2,
          name: 'Port Scan',
          pattern: 'iptables.*DROP',
          severity: 'medium', 
          threshold: 5,
          time_window: 60,
          description: 'Multiple blocked connections'
        }
      ]);
    }

    // Agrupar logs
    const logGroups = {};
    logs.forEach(item => {
      const log = item.json;
      const key = `${log.type}_${log.source_ip || 'unknown'}`;
      if (!logGroups[key]) logGroups[key] = [];
      logGroups[key].push(log);
    });

    const alerts = [];

    // Aplicar reglas
    for (const rule of rules) {
      for (const [key, logGroup] of Object.entries(logGroups)) {
        const matchingLogs = logGroup.filter(log => {
          try {
            const regex = new RegExp(rule.pattern, 'i');
            return regex.test(log.raw);
          } catch (e) {
            console.log(`Error in regex for rule ${rule.name}:`, e.message);
            return false;
          }
        });

        if (matchingLogs.length >= rule.threshold) {
          const timestamps = matchingLogs.map(log => new Date(log.timestamp).getTime());
          const timeRange = Math.max(...timestamps) - Math.min(...timestamps);

          if (timeRange <= rule.time_window * 1000) {
            alerts.push({
              rule_id: rule.id,
              rule_name: rule.name,
              severity: rule.severity,
              category: matchingLogs[0].category || 'general',
              source_ip: matchingLogs[0].source_ip || 'unknown',
              target_host: 'server1',
              event_count: matchingLogs.length,
              description: `${rule.description} - ${matchingLogs.length} eventos desde ${matchingLogs[0].source_ip || 'unknown'}`,
              raw_logs: matchingLogs.map(log => log.raw),
              first_occurrence: new Date(Math.min(...timestamps)).toISOString(),
              last_occurrence: new Date(Math.max(...timestamps)).toISOString(),
              from_database: true
            });
          }
        }
      }
    }

    await client.end();

    return alerts;

  } catch (error) {
    console.error('PostgreSQL Error:', error.message);
    throw error; 
  }
}

try {
  const alerts = await processWithPostgreSQL();

  if (alerts.length === 0) {
    alerts.push({
      rule_id: 0,
      rule_name: 'Monitoring Active',
      severity: 'info',
      category: 'system',
      description: 'Security monitoring active - no alerts detected',
      from_database: false
    });
  }

  console.log(`Generated ${alerts.length} alerts`);
  return alerts.map(alert => ({ json: alert }));

} catch (error) {
  console.error('Failed to process with PostgreSQL:', error.message);

  const fallbackAlerts = [{
    rule_id: 999,
    rule_name: 'Database Connection Failed',
    severity: 'warning',
    category: 'system',
    description: `Using fallback rules: ${error.message}`,
    from_database: false
  }];

  return fallbackAlerts.map(alert => ({ json: alert }));
}

