    priority = 'urgent';
    slackColor = '#ff0000'; // rojo
  } else if (alert.severity === 'high') {
    priority = 'high';
    slackColor = '#ff9900'; // naranja
  } else if (alert.severity === 'medium') {
    slackColor = '#ffff00'; // amarillo
  }

  notifications.push({
    json: {
      type: 'email',
      channel: 'email',
      to: 'security-team@example.com',
      subject: `[${alert.severity.toUpperCase()}] ${alert.rule_name || 'Security Alert'}`,
      body: `
SECURITY ALERT REPORT
=====================
Alert ID: ${alert.alert_id || 'N/A'}
Timestamp: ${new Date().toISOString()}
Severity: ${alert.severity}
Category: ${alert.category || 'N/A'}

DETAILS:
--------
Rule: ${alert.rule_name || 'Unknown'}
Source IP: ${alert.source_ip || 'Unknown'}
