const items = $input.all();

// 1. Verificar si es IP privada
const ip = items[0].json.source_ip || '0.0.0.0';
const isPrivateIP = ip.startsWith('10.') || 
                   ip.startsWith('192.168.') || 
                   (ip.startsWith('172.') && parseInt(ip.split('.')[1]) >= 16 && parseInt(ip.split('.')[1]) <= 31);

// 2. Base de datos SIMULADA de threat intelligence
const threatDB = {
  '192.168.1.50': { 
    reputation: 'malicious', 
    tags: ['sql_injection', 'web_attack'],
    confidence: 0.85
  },
  '10.0.0.10': { 
    reputation: 'suspicious', 
    tags: ['port_scan'],
    confidence: 0.65
  }
};

// 3. Aplicar enriquecimiento
items[0].json.threat_intel = {
  is_private_ip: isPrivateIP,
  threat_info: threatDB[ip] || { 
    reputation: 'unknown', 
    tags: [], 
    confidence: 0 
  },
  geo_info: {
    country: isPrivateIP ? 'INTERNAL' : 'UNKNOWN',
    asn: 'N/A'
  },
  enrichment_timestamp: new Date().toISOString()
};

// 4. Calcular risk_score mejorado
let riskScore = 0;

// Base por severity
switch(items[0].json.severity) {
  case 'critical': riskScore = 90; break;
  case 'high': riskScore = 70; break;
  case 'medium': riskScore = 50; break;
  default: riskScore = 30;
}

// Bonificación por threat intelligence
if (items[0].json.threat_intel.threat_info.reputation === 'malicious') {
  riskScore += 20;
} else if (items[0].json.threat_intel.threat_info.reputation === 'suspicious') {
  riskScore += 10;
}

// Bonificación por múltiples eventos
if (items[0].json.event_count && items[0].json.event_count > 3) {
  riskScore += 15;
}

items[0].json.risk_score = Math.min(100, riskScore);
items[0].json.risk_level = riskScore >= 80 ? 'CRITICAL' : 
                          riskScore >= 60 ? 'HIGH' : 
                          riskScore >= 40 ? 'MEDIUM' : 'LOW';

return items;
